
public class IncDec {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=10;
		System.out.println("Initial Value: "+a);
		++a;
		System.out.println("PreIncrement: "+a);//1
		a++;
		System.out.println("PostIncrement: "+a);//11
		//a=12
		--a;
		System.out.println("PreDecrement: "+a);//11
		a--;
		System.out.println("PostDecrement: "+a);//11
		

	}

}
